	private static final Map<String, ClueScroll> clues = new HashMap<>();

	private int[] ids;
	private String text;
	private String npc;
	private String location;
	private String answer;
	private ClueScrollType type;

	static
	{
		for (ClueScroll s : values())
		{
			clues.put(s.getText().toLowerCase(), s);
		}
	}

	ClueScroll(int[] ids, String text, String npc, String location, String answer, ClueScrollType type)
	{
		this.ids = ids;
		this.text = text;
		this.npc = npc;
		this.location = location;
		this.answer = answer;
		this.type = type;
	}

	ClueScroll(String text, String npc, String location, String answer, ClueScrollType type)
	{
		this(null, text, npc, location, answer, type);
	}

	ClueScroll(String text, String npc, String location, ClueScrollType type)
	{
		this(null, text, npc, location, null, type);
	}

	ClueScroll(String text, String answer, ClueScrollType type)
	{
		this(null, text, null, null, answer, type);
	}

	ClueScroll(int[] ids, String text, ClueScrollType type)
	{
		this(ids, text, null, null, null, type);
	}

	public int[] getIds()
	{
		return ids;
	}

	public String getText()
	{
		return text;
	}

	public String getNpc()
	{
		return npc;
	}

	public String getLocation()
	{
		return location;
	}

	public String getAnswer()
	{
		return answer;
	}

	public ClueScrollType getType()
	{
		return type;
	}

	public static ClueScroll forText(String text)
	{
		return clues.get(text);
	}
